/* Filtro de pestañas del Catálogo */
function openTab(evt, tab) {	
    // Declare all variables
    var i, tabcontent, tablinks;

    // Get all elements with class="tabcontent" and hide them
    tabcontent = document.getElementsByClassName("tabcontent");
    for (i = 0; i < tabcontent.length; i++) {
        tabcontent[i].style.display = "none";
    }

    // Get all elements with class="tablinks" and remove the class "active"
    tablinks = document.getElementsByClassName("tablinks");
    for (i = 0; i < tablinks.length; i++) {
        tablinks[i].className = tablinks[i].className.replace(" active", "");
    }

    // Show the current tab, and add an "active" class to the button that opened the tab
    document.getElementById(tab).style.display = "block";
    if ("load" == evt.type) {
    	document.getElementById("bikesTabButton").className += " active";
    } else {
    	evt.currentTarget.className += " active";
    }
    document.getElementById("tab").style.borderBottom = "0";
}

function closeTab(evt, element) {
	document.getElementById("tab").style.borderBottom = "1px solid red";
	element.parentElement.style.display = 'none';
}

function minFilter(evt, element, max, min) {
	document.getElementById(element).style.display = "none";
	document.getElementById(min).style.display = "none";
	document.getElementById(max).style.display = "block";
}

function maxFilter(evt, element, min, max) {
	document.getElementById(element).style.display = "block";
	document.getElementById(max).style.display = "none";
	document.getElementById(min).style.display = "block";
}

function markChildren(evt, element, type) {
	var childrens;
	
	childrens = document.getElementsByName(type);
	
	for (i = 0; i < childrens.length; i++) {
		if (childrens[i].id != element.id) {
			childrens[i].checked = element.checked;
		}
    }
}